library(ggplot2)
cycP_mut_rate <- data.frame(group=c("a", "a", "a", "b","b","b", "c","c","c", "d","d","d", "e","e","e"), type=c("bs", "aindel", "comb","bs", "aindel", "comb","bs", "aindel", "comb","bs", "aindel", "comb","bs", "aindel", "comb"), mut=c(2.0, 11.7, 13.7, 25.4, 0, 25.4, 30.5, 0, 30.5, 31.1, 4.1, 35.2, 33.1, 5.5, 38.6), ci.low=c(NA, NA, 6.4, NA, NA, 6.8, NA, NA, 0, NA, NA, 0, NA, NA, 0), ci.high=c(NA, NA, 21.8, NA, NA, 50.8, NA, NA, 55.6, NA, NA, 64.1, NA, NA, 64.1))
asex_mut_rate <- data.frame(group=c("a", "a", "a", "b","b","b", "c","c","c", "d","d","d", "e","e","e"), type=c("bs", "aindel", "comb","bs", "aindel", "comb","bs", "aindel", "comb","bs", "aindel", "comb","bs", "aindel", "comb"), mut=c(4.3, 13.0, 17.3, 18.6, 8.5, 27.2, 19.6, 9.5, 29.1, 19.6, 9.5, 29.1, 19.6, 9.5, 29.1), ci.low=c(NA, NA, 8.3, NA, NA, 19, NA, NA, 12.3, NA, NA, 12.2, NA, NA, 10.9), ci.high=c(NA, NA, 27.1, NA, NA, 44.4, NA, NA, 45.9, NA, NA, 45.9, NA, NA, 47.5))
